# ---- declare-globals ---------------------------------------------------------
config <- config::get()

# ---- load-data ---------------------------------------------------------------
dataset_list <- OECD::get_datasets()
search_dataset("health", data = dataset_list)

# ---- define-queries ---------------------

if( !fs::dir_exists(config$path_oecd_health_chapter) )
  fs::dir_create(config$path_oecd_health_chapter)

# Health Care Resources
dataset <- "HEALTH_REAC"
dstruc <- OECD::get_data_structure(dataset)
str(dstruc, max.level = 1)
query_url_SDMX <- "https://stats.oecd.org/restsdmx/sdmx.ashx/GetData/HEALTH_..AUS+AUT+BEL+CAN+CHL+CZE+DNK+EST+FIN+FRA+DEU+GRC+HUN+ISL+IRL+ISR+ITA+JPN+KOR+LVA+LTU+LUX+MEX+NLD+NZL+NOR+POL+PRT+SVK+SVN+ESP+SWE+CHE+TUR+GBR+USA+NMEC+BRA+CHN+COL+CRI+IND+IDN+RUS+ZAF/all?startTime=2015&endTime=2018"
data <- rsdmx::readSDMX(query_url_SDMX) %>% as.data.frame()

ls_health_resources <- list(
  "name" = dataset
  ,"structure" = dstruc
  ,"url" = query_url_SDMX
  ,"data" = data
)
pryr::object_size(ls_health_resources)
readr::write_rds(ls_health_resources, fs::path(config$path_oecd_health_chapter, "health_resources.rds"))


